CREATE TABLE ability (id varchar(10) NOT NULL COMMENT '能力项编码', name varchar(30) COMMENT '能力项名称', domain varchar(30) COMMENT '能力项所属领域', description varchar(300) COMMENT '能力项描述', assess_method varchar(100) COMMENT '检核方式', option_a varchar(500) COMMENT '检核标准-A', option_b varchar(500) COMMENT '检核标准-B', option_c varchar(500) COMMENT '检核标准-C', option_d varchar(500) COMMENT '检核标准-D', sort_index smallint COMMENT '排序索引', status smallint(1) COMMENT '状态', creator varchar(30) COMMENT '创建人', create_time timestamp DEFAULT '0000-00-00 00:00:00' COMMENT '创建日期', modifier varchar(30) COMMENT '修改人', modify_time timestamp DEFAULT '0000-00-00 00:00:00' COMMENT '修改日期', PRIMARY KEY (id)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='能力项检核信息表';
CREATE TABLE assessment_report (assess_report_id int NOT NULL AUTO_INCREMENT COMMENT '检核评估报告编码', trainee_id int NOT NULL COMMENT '学生编码', occupation_id int NOT NULL COMMENT '职业编码', potential varchar(200) COMMENT '职业潜能', limits varchar(200) COMMENT '职业限制', work_context varchar(200) COMMENT '工作背景', personal_will varchar(200) COMMENT '个人意愿', parent_will varchar(200) COMMENT '家长意愿', other varchar(200) COMMENT '其他', place_advice varchar(200) COMMENT '综合安置建议', track_date timestamp DEFAULT '0000-00-00 00:00:00' COMMENT '安置追踪日期', tracker varchar(30) COMMENT '安置追踪者', track_result varchar(200) COMMENT '安置追踪结果', designer varchar(30) COMMENT '设计者', designer_title varchar(30) COMMENT '设计者职称', institution varchar(50) COMMENT '评测单位', institution_phone varchar(30) COMMENT '电话', executor varchar(30) COMMENT '执行者', executor_title varchar(30) COMMENT '执行者职称', institution_address varchar(50) COMMENT '单位地址', generator varchar(30) COMMENT '检核报告处理人', generate_time timestamp DEFAULT '0000-00-00 00:00:00' COMMENT '检核报告生成时间', PRIMARY KEY (assess_report_id, trainee_id, occupation_id)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='职业能力检核评估报告';
CREATE TABLE assessment_result (assess_task_id int NOT NULL COMMENT '检核任务编码', trainee_id int NOT NULL COMMENT '学生编码', ability_id varchar(10) NOT NULL COMMENT '检核能力编码', assess_option varchar(1) COMMENT '检核能力打分', PRIMARY KEY (assess_task_id, trainee_id, ability_id)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='职业能力检核结果表';
CREATE TABLE enum_dic (code int NOT NULL COMMENT '编码', name varchar(30) COMMENT '名称', value varchar(30) COMMENT '值', groups varchar(30) COMMENT '枚举分组', sort_index smallint COMMENT '排序索引', description varchar(300) COMMENT '描述', creator varchar(30) COMMENT '创建人', create_time timestamp DEFAULT '0000-00-00 00:00:00' COMMENT '创建日期', PRIMARY KEY (code)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='枚举字典表';
CREATE TABLE occupation (id int NOT NULL COMMENT '职业编码', name varchar(50) COMMENT '职业名称', description varchar(300) COMMENT '职业描述', status smallint(1) COMMENT '状态', creator varchar(30) COMMENT '创建人', create_time timestamp DEFAULT '0000-00-00 00:00:00' COMMENT '创建日期', modifier varchar(30) COMMENT '修改人', modify_time timestamp DEFAULT '0000-00-00 00:00:00' COMMENT '修改日期', PRIMARY KEY (id)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='职业信息表';
CREATE TABLE occupation_ability (occupation_id int NOT NULL COMMENT '职业编码', ability_id varchar(10) NOT NULL COMMENT '能力项编码', necessary_level smallint(1) COMMENT '是否必要', criterion_score varchar(1) COMMENT '检核标准', creator varchar(30) COMMENT '创建人', create_time timestamp DEFAULT '0000-00-00 00:00:00' COMMENT '创建日期', modifier varchar(30) COMMENT '修改人', modify_time timestamp DEFAULT '0000-00-00 00:00:00' COMMENT '修改日期', PRIMARY KEY (occupation_id, ability_id)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='职业能力检核标准配置表';
CREATE TABLE sysuser (id int NOT NULL COMMENT '用户编码', sex varchar(1) COMMENT '性别', birthday varchar(30) COMMENT '出生日期', name varchar(30) COMMENT '用户名称', password varchar(50) COMMENT '登录密码', status smallint(1) COMMENT '状态', creator varchar(30) COMMENT '创建人', create_time timestamp DEFAULT '0000-00-00 00:00:00' COMMENT '创建日期', modifier varchar(30) COMMENT '修改人', modify_time timestamp DEFAULT '0000-00-00 00:00:00' COMMENT '修改日期', PRIMARY KEY (id)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='系统用户表';
CREATE TABLE trainee (id int NOT NULL COMMENT '学生编码', name varchar(30) COMMENT '姓名', sex varchar(1) COMMENT '性别', birthday varchar(30) COMMENT '出生日期', id_card varchar(30) COMMENT '身份证号', qq varchar(30) COMMENT 'QQ号码', weixin varchar(30) COMMENT '微信号码', e_mail varchar(30) COMMENT '电子邮件', guardian varchar(30) COMMENT '监护人', relationship varchar(30) COMMENT '与本人关系', address varchar(100) COMMENT '住址', phone varchar(30) COMMENT '联系方式', disabled_type varchar(30) COMMENT '主要障碍类型', disabled_level varchar(30) COMMENT '障碍等级', disabled_reason varchar(100) COMMENT '障碍原因', other_disabled varchar(100) COMMENT '其他障碍', degree varchar(30) COMMENT '教育程度', education_level varchar(50) COMMENT '教育水平', have_trained smallint(1) COMMENT '是否接受过职业训练', trained_intro varchar(300) COMMENT '职业训练说明', is_worked smallint(1) COMMENT '是否有工作经验', worked_intro varchar(300) COMMENT '工作经验说明', expect_work varchar(100) COMMENT '希望从事的工作', father varchar(30) COMMENT '父亲', father_degree varchar(30) COMMENT '教育程度', father_job varchar(30) COMMENT '职业', mother varchar(30) COMMENT '母亲', monther_degree varchar(30) COMMENT '教育程度', mother_job varchar(30) COMMENT '职业', parent_expect varchar(100) COMMENT '父母期望', status smallint(1) COMMENT '状态', creator varchar(30) COMMENT '创建人', create_time timestamp DEFAULT '0000-00-00 00:00:00' COMMENT '创建日期', modifier varchar(30) COMMENT '修改人', modify_time timestamp DEFAULT '0000-00-00 00:00:00' COMMENT '修改日期', PRIMARY KEY (id)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='学生（残障儿童）信息表';
CREATE TABLE trainee_sysuser_relationship (student int NOT NULL COMMENT '学生编码', assessor int NOT NULL COMMENT '检核人', assess_date timestamp DEFAULT '0000-00-00 00:00:00' COMMENT '检核时间', flag smallint NOT NULL COMMENT '检核报告标志', PRIMARY KEY (student, assessor, flag)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='学生检核人关系表';